// This script creates notification sound files from base64 encoded data
(function() {
  // Function to convert base64 to Blob
  function base64ToBlob(base64, mimeType) {
    const byteString = atob(base64);
    const arrayBuffer = new ArrayBuffer(byteString.length);
    const uint8Array = new Uint8Array(arrayBuffer);
    
    for (let i = 0; i < byteString.length; i++) {
      uint8Array[i] = byteString.charCodeAt(i);
    }
    
    return new Blob([arrayBuffer], { type: mimeType });
  }
  
  // Function to save blob as file
  function saveBlobAsFile(blob, fileName) {
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = fileName;
    a.style.display = 'none';
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  }
  
  // Base64 encoded MP3 sounds (very short, simple tones)
  const sounds = {
    // Default notification sound (soft bell)
    notification: {
      data: "",
      type: "audio/mp3"
    },
    // Success sound (high pleasant ding)
    success: {
      data: "",
      type: "audio/mp3"
    },
    // Error sound (low double tone)
    error: {
      data: "",
      type: "audio/mp3"
    },
    // Warning sound (medium tone)
    warning: {
      data: "",
      type: "audio/mp3"
    },
    // Info sound (soft tone)
    info: {
      data: "",
      type: "audio/mp3"
    }
  };
  
  // Create and save each sound file
  for (const [name, sound] of Object.entries(sounds)) {
    const blob = base64ToBlob(sound.data, sound.type);
    saveBlobAsFile(blob, `${name}.mp3`);
  }
  
  console.log('Notification sounds created successfully!');
})();